DROP TRIGGER IF EXISTS `increaseBookAmount`;
DELIMITER ;;
CREATE TRIGGER `increaseBookAmount` AFTER INSERT ON `bt` FOR EACH ROW BEGIN
	UPDATE booktype SET amount=amount+1 WHERE new.booktypeid=booktype.id;
    END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `deleteBookAmount`;
DELIMITER ;;
CREATE TRIGGER `deleteBookAmount` AFTER DELETE ON `bt` FOR EACH ROW BEGIN
	UPDATE booktype SET amount=amount-1 WHERE old.booktypeid=booktype.id;
    END
;;
DELIMITER ;
